package warmingup;

public class StarPyramid {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=1;
		while(x<10){
			int y=0;
			while(y<x)
			{
				System.out.print("*");
					y++;
				}
			System.out.println();
			x++;
		}
			
		}

		
}

